import { createResourceState, createComputed } from "solid-js";
import { render } from "solid-js/web";

const fetchUser = (id) => () =>
  id
    ? fetch(`https://swapi.dev/api/people/${id}/`).then((r) => r.json())
    : undefined;

const App = () => {
  const [state, load, setState] = createResourceState();
  createComputed(() =>
    load({
      user: fetchUser(state.userId)
    })
  );

  return (
    <>
      <input
        type="number"
        min="1"
        placeholder="Enter Numeric Id"
        onInput={(e) => setState({ userId: e.target.value })}
      />
      <span>{state.loading.user && "Loading..."}</span>
      <div>
        <code>{JSON.stringify(state.user)}</code>
      </div>
    </>
  );
};

render(App, document.getElementById("app"));
